        public static IEnumerable<Invoice> GetInvoices() {

            List<Invoice> invoices = new List<Invoice>
            {
                // Invoice for Line items with InvoiceId -1
                new Invoice { Id = -1, Date = DateTime.Now.AddDays(-1), DueDate = DateTime.Now.AddDays(29), PaymentTerms = "Net 29", From = "ABC Corporation 1", BillTo = "Customer 1", Notes = "Thank you for your business - Invoice 1", Terms = "Net 29", CustomerId = -1 },
                // Invoice for Line items with InvoiceId -2
                new Invoice { Id = -2, Date = DateTime.Now.AddDays(-2), DueDate = DateTime.Now.AddDays(28), PaymentTerms = "Net 28", From = "ABC Corporation 2", BillTo = "Customer 2", Notes = "Thank you for your business - Invoice 2", Terms = "Net 28", CustomerId = -2 },
                // Invoice for Line items with InvoiceId -3
                new Invoice { Id = -3, Date = DateTime.Now.AddDays(-3), DueDate = DateTime.Now.AddDays(27), PaymentTerms = "Net 27", From = "ABC Corporation 3", BillTo = "Customer 3", Notes = "Thank you for your business - Invoice 3", Terms = "Net 27", CustomerId = -3 },
                // Invoice for Line items with InvoiceId -4
                new Invoice { Id = -4, Date = DateTime.Now.AddDays(-4), DueDate = DateTime.Now.AddDays(26), PaymentTerms = "Net 26", From = "ABC Corporation 4", BillTo = "Customer 4", Notes = "Thank you for your business - Invoice 4", Terms = "Net 26", CustomerId = -4 },
                // Invoice for Line items with InvoiceId -5
                new Invoice { Id = -5, Date = DateTime.Now.AddDays(-5), DueDate = DateTime.Now.AddDays(25), PaymentTerms = "Net 25", From = "ABC Corporation 5", BillTo = "Customer 5", Notes = "Thank you for your business - Invoice 5", Terms = "Net 25", CustomerId = -5 },
                // Invoice for Line items with InvoiceId -6
                new Invoice { Id = -6, Date = DateTime.Now.AddDays(-6), DueDate = DateTime.Now.AddDays(24), PaymentTerms = "Net 24", From = "ABC Corporation 6", BillTo = "Customer 6", Notes = "Thank you for your business - Invoice 6", Terms = "Net 24", CustomerId = -6 },
                // Invoice for Line items with InvoiceId -7
                new Invoice { Id = -7, Date = DateTime.Now.AddDays(-7), DueDate = DateTime.Now.AddDays(23), PaymentTerms = "Net 23", From = "ABC Corporation 7", BillTo = "Customer 7", Notes = "Thank you for your business - Invoice 7", Terms = "Net 23", CustomerId = -7 },
                // Invoice for Line items with InvoiceId -8
                new Invoice { Id = -8, Date = DateTime.Now.AddDays(-8), DueDate = DateTime.Now.AddDays(22), PaymentTerms = "Net 22", From = "ABC Corporation 8", BillTo = "Customer 8", Notes = "Thank you for your business - Invoice 8", Terms = "Net 22", CustomerId = -8 },
                // Invoice for Line items with InvoiceId -9
                new Invoice { Id = -9, Date = DateTime.Now.AddDays(-9), DueDate = DateTime.Now.AddDays(21), PaymentTerms = "Net 21", From = "ABC Corporation 9", BillTo = "Customer 9", Notes = "Thank you for your business - Invoice 9", Terms = "Net 21", CustomerId = -9 },
                // Invoice for Line items with InvoiceId -10
                new Invoice { Id = -10, Date = DateTime.Now.AddDays(-10), DueDate = DateTime.Now.AddDays(20), PaymentTerms = "Net 20", From = "ABC Corporation 10", BillTo = "Customer 10", Notes = "Thank you for your business - Invoice 10", Terms = "Net 20", CustomerId = -1 },
                // Invoice for Line items with InvoiceId -11
                new Invoice { Id = -11, Date = DateTime.Now.AddDays(-11), DueDate = DateTime.Now.AddDays(19), PaymentTerms = "Net 19", From = "ABC Corporation 11", BillTo = "Customer 11", Notes = "Thank you for your business - Invoice 11", Terms = "Net 19", CustomerId = -2 },
                // Invoice for Line items with InvoiceId -12
                new Invoice { Id = -12, Date = DateTime.Now.AddDays(-12), DueDate = DateTime.Now.AddDays(18), PaymentTerms = "Net 18", From = "ABC Corporation 12", BillTo = "Customer 12", Notes = "Thank you for your business - Invoice 12", Terms = "Net 18", CustomerId = -3 },
                // Invoice for Line items with InvoiceId -13
                new Invoice { Id = -13, Date = DateTime.Now.AddDays(-13), DueDate = DateTime.Now.AddDays(17), PaymentTerms = "Net 17", From = "ABC Corporation 13", BillTo = "Customer 13", Notes = "Thank you for your business - Invoice 13", Terms = "Net 17", CustomerId = -4 },
                // Invoice for Line items with InvoiceId -14
                new Invoice { Id = -14, Date = DateTime.Now.AddDays(-14), DueDate = DateTime.Now.AddDays(16), PaymentTerms = "Net 16", From = "ABC Corporation 14", BillTo = "Customer 14", Notes = "Thank you for your business - Invoice 14", Terms = "Net 16", CustomerId = -5 },
                // Invoice for Line items with InvoiceId -15
                new Invoice { Id = -15, Date = DateTime.Now.AddDays(-15), DueDate = DateTime.Now.AddDays(15), PaymentTerms = "Net 15", From = "ABC Corporation 15", BillTo = "Customer 15", Notes = "Thank you for your business - Invoice 15", Terms = "Net 15", CustomerId = -6 },
            };

            foreach (var invoice in invoices)
            {
                // Find line items for the current invoice ID
                var itemsForInvoice = LineItems.Where(item => item.InvoiceId == invoice.Id).ToList();

                // Calculate SubTotal as the sum of TotalPrice in line items
                invoice.SubTotal = double.Round(itemsForInvoice.Sum(item => item.TotalPrice ?? 0.0),2);

                invoice.Tax = new Random().Next(0, 5);
                var totalAfterTax = invoice.SubTotal * (1 + invoice.Tax / 100);

                // Calculate Total as SubTotal plus Tax
                invoice.Total = double.Round(totalAfterTax, 2);
            }

            return invoices;

        }
        
    }
